mapper.select:
SELECT COUNT(*)
  FROM category
 WHERE category_name = #{category_name}

mapper.update:
UPDATE category
   SET (mod_dt
		, mod_id
   		, category_name
		, order_no
		, use_yn
   	   ) = (
		now()
		, #{mod_id}
		, #{category_name}
		, #{order_no}
		, #{use_yn}
	   )
 WHERE category_cd = #{category_cd}
