mapper.insert:
INSERT INTO post
(title, content, category_cd, use_yn, comment_yn, reg_id, delegate_img, post_theme_cd

	,reg_dt

)
VALUES
(#{title}, #{content}, #{category_cd}, #{use_yn}, ${comment_yn}, #{reg_id}, #{delegate_img}, #{post_theme_cd}

	,#{reg_dt}

)

	SELECT currval('post_post_cd_seq')

mapper.update:
UPDATE post
   SET (mod_dt
		, mod_id
		, title
		, content
		, use_yn
		, comment_yn
		, delegate_img
		, post_theme_cd
		) = (
		now()
		, #{mod_id}
		, #{title}
		, #{content}
		, #{use_yn}
		, #{comment_yn}
		, #{delegate_img}
		, #{post_theme_cd}
	)
 WHERE post_cd = #{post_cd}

mapper.select:

	 WHERE use_yn = TRUE

  	   AND category_cd = #{category_cd}


